  return (
    <>
      <h2 style={{ color: "#22c55e" }}>ðŸ›  ADMIN DASHBOARD</h2>

      <p>
        ðŸ“Š Tá»•ng sá»‘ Ä‘Æ¡n hÃ ng: <b>{orderCount}</b>
      </p>

      <OrderForm
        productId={productId}
        setProductId={setProductId}
        amountVND={amountVND}
