import { useState } from 'react';

function OrderList({ orders }) {
  const [searchProductId, setSearchProductId] = useState('');

  // üîç FILTER THEO PRODUCT ID
  const filteredOrders = orders.filter((o) =>
    searchProductId
      ? o.productId.includes(searchProductId)
      : true
  );

  return (
    <div style={styles.wrapper}>
      <h3 style={styles.heading}>üì¶ Danh s√°ch ƒë∆°n h√†ng</h3>

      {/* üîé SEARCH INPUT */}
      <input
        placeholder="üîç T√¨m theo Product ID..."
        value={searchProductId}
        onChange={(e) => setSearchProductId(e.target.value)}
        style={styles.search}
      />

      {filteredOrders.length === 0 && (
        <p style={{ marginTop: 20, color: '#94a3b8' }}>
          ‚ùå Kh√¥ng t√¨m th·∫•y ƒë∆°n h√†ng
        </p>
      )}

      {filteredOrders.map((o) => (
        <div key={o.orderId} style={styles.card}>
          <h4>üì¶ Order #{o.orderId}</h4>
          <p>üîß Product ID: <b>{o.productId}</b></p>
          <p>üí∞ Gi√° (VND): {o.amountVND}</p>
          <p>üë§ Buyer: {o.buyer}</p>
          <p>üïí Th·ªùi gian: {o.createdAt}</p>
        </div>
      ))}
    </div>
  );
}

/* ================= STYLE ================= */
const styles = {
  wrapper: {
    width: '100%',
    maxWidth: 600,
    marginTop: 30
  },
  heading: {
    color: '#38bdf8',
    marginBottom: 10
  },
  search: {
    width: '100%',
    padding: 10,
    borderRadius: 8,
    border: '1px solid #334155',
    background: '#020617',
    color: '#e5e7eb',
    marginBottom: 20
  },
  card: {
    background: '#020617',
    border: '1px solid #334155',
    borderRadius: 10,
    padding: 16,
    marginBottom: 15,
    textAlign: 'left'
  }
};

export default OrderList;
