import React from 'react';

function OrderList({ orders }) {
  if (!orders || orders.length === 0) {
    return (
      <div style={styles.empty}>
        üì≠ Ch∆∞a c√≥ ƒë∆°n h√†ng n√†o tr√™n blockchain
      </div>
    );
  }

  return (
    <div style={styles.wrapper}>
      <h2 style={styles.heading}>üì¶ Danh s√°ch ƒë∆°n h√†ng On-chain</h2>

      {orders.map((o) => (
        <div key={o.orderId} style={styles.card}>
          <div style={styles.row}>
            <span style={styles.label}>Order ID</span>
            <span style={styles.value}>#{o.orderId}</span>
          </div>

          <div style={styles.row}>
            <span style={styles.label}>Product ID</span>
            <span style={styles.value}>{o.productId}</span>
          </div>

          <div style={styles.row}>
            <span style={styles.label}>Gi√° (VND)</span>
            <span style={styles.value}>
              {Number(o.amountVND).toLocaleString('vi-VN')} ‚Ç´
            </span>
          </div>

          <div style={styles.row}>
            <span style={styles.label}>Buyer</span>
            <span style={styles.address}>
              {o.buyer.slice(0, 6)}...{o.buyer.slice(-4)}
            </span>
          </div>

          <div style={styles.row}>
            <span style={styles.label}>Th·ªùi gian</span>
            <span style={styles.value}>{o.createdAt}</span>
          </div>
        </div>
      ))}
    </div>
  );
}

/* ================= STYLE ================= */

const styles = {
  wrapper: {
    width: '100%',
    maxWidth: 700,
    marginTop: 30
  },
  heading: {
    marginBottom: 16,
    color: '#38bdf8'
  },
  card: {
    background: '#020617',
    border: '1px solid #334155',
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
    boxShadow: '0 0 15px rgba(56,189,248,0.15)'
  },
  row: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: 6
  },
  label: {
    color: '#94a3b8',
    fontSize: 14
  },
  value: {
    color: '#e5e7eb',
    fontWeight: 500
  },
  address: {
    fontFamily: 'monospace',
    color: '#22c55e'
  },
  empty: {
    marginTop: 30,
    padding: 20,
    background: '#020617',
    borderRadius: 10,
    border: '1px dashed #334155',
    color: '#94a3b8'
  }
};

export default OrderList;
