import { ethers } from "ethers";
import abi from "../abi/abi.json";

// ðŸ”´ THAY báº±ng Ä‘á»‹a chá»‰ contract báº¡n Ä‘Ã£ deploy (Sepolia)
const CONTRACT_ADDRESS = "0xPASTE_CONTRACT_ADDRESS_HERE";

/*
  Láº¥y instance cá»§a WarrantyManager contract
*/
const getWarrantyContract = async () => {
  if (!window.ethereum) {
    throw new Error("MetaMask not found");
  }

  const provider = new ethers.BrowserProvider(window.ethereum);
  const signer = await provider.getSigner();

  return new ethers.Contract(
    CONTRACT_ADDRESS,
    abi,
    signer
  );
};

/*
  Láº¥y danh sÃ¡ch warrantyId theo Ä‘á»‹a chá»‰ vÃ­
*/
export const getMyWarranties = async (address) => {
  const contract = await getWarrantyContract();
  return await contract.getMyWarranties(address);
};

/*
  ðŸ”¥ Láº¤Y CHI TIáº¾T 1 Báº¢O HÃ€NH
*/
export const getWarrantyDetail = async (warrantyId) => {
  const contract = await getWarrantyContract();
  return await contract.getWarranty(warrantyId);
};
