import './App.css';
import { useState, useEffect } from 'react';
import { connectWallet } from './services/wallet';
import {
  getOrderCount,
  getOrderByIndex,
  createOrder
} from './services/blockchain';

const SEPOLIA_CHAIN_ID = '0xaa36a7'; // 11155111

function App() {
  const [wallet, setWallet] = useState('');
  const [orders, setOrders] = useState([]);
  const [orderCount, setOrderCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // üìù FORM STATE
  const [productId, setProductId] = useState('');
  const [amountVND, setAmountVND] = useState('');
  const [txHash, setTxHash] = useState('');

  /* ================= AUTO RELOAD KHI ƒê·ªîI M·∫†NG ================= */
  useEffect(() => {
    if (!window.ethereum) return;

    const handleChainChanged = () => {
      window.location.reload();
    };

    window.ethereum.on('chainChanged', handleChainChanged);
    return () =>
      window.ethereum.removeListener(
        'chainChanged',
        handleChainChanged
      );
  }, []);

  /* ================= LOAD ORDER ================= */
  const loadOrders = async () => {
    const countBN = await getOrderCount();
    const total = Number(countBN);
    setOrderCount(total);

    if (total === 0) {
      setOrders([]);
      return;
    }

    const list = [];
    for (let i = 1; i <= total; i++) {
      const o = await getOrderByIndex(i);
      list.push({
        orderId: o.orderId.toString(),
        productId: o.productId.toString(),
        amountVND: o.amountVND.toString(),
        buyer: o.buyer,
        createdAt: new Date(
          Number(o.createdAt) * 1000
        ).toLocaleString('vi-VN')
      });
    }

    setOrders(list);
  };

  /* ================= CONNECT WALLET ================= */
  const handleConnectWallet = async () => {
    try {
      setLoading(true);
      setError('');

      const address = await connectWallet();
      if (!address) return;
      setWallet(address);

      const chainId = await window.ethereum.request({
        method: 'eth_chainId'
      });

      if (chainId !== SEPOLIA_CHAIN_ID) {
        await window.ethereum.request({
          method: 'wallet_switchEthereumChain',
          params: [{ chainId: SEPOLIA_CHAIN_ID }]
        });
        return;
      }

      await loadOrders();
    } catch (err) {
      console.error(err);
      setError('‚ùå Kh√¥ng ƒë·ªçc ƒë∆∞·ª£c d·ªØ li·ªáu t·ª´ blockchain');
    } finally {
      setLoading(false);
    }
  };

  /* ================= CREATE ORDER ================= */
  const handleCreateOrder = async () => {
    if (!productId || !amountVND) {
      alert('Nh·∫≠p ƒë·∫ßy ƒë·ªß Product ID v√† Gi√°');
      return;
    }

    try {
      setLoading(true);
      setTxHash('');

      const hash = await createOrder(
        Number(productId),
        Number(amountVND)
      );

      setTxHash(hash);
      setProductId('');
      setAmountVND('');

      await loadOrders();
    } catch (err) {
      console.error(err);
      alert('‚ùå T·∫°o order th·∫•t b·∫°i');
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>
        üîê H·ªÜ TH·ªêNG ƒê∆†N H√ÄNG / B·∫¢O H√ÄNH BLOCKCHAIN
      </h1>

      <p style={styles.subtitle}>
        Minh b·∫°ch d·ªØ li·ªáu ƒë∆°n h√†ng b·∫±ng Smart Contract
      </p>

      <button style={styles.button} onClick={handleConnectWallet}>
        {wallet
          ? `ƒê√£ k·∫øt n·ªëi: ${wallet.slice(0, 6)}...${wallet.slice(-4)}`
          : 'K·∫øt n·ªëi MetaMask'}
      </button>

      {loading && <p>‚è≥ ƒêang x·ª≠ l√Ω...</p>}
      {error && <p style={{ color: '#ef4444' }}>{error}</p>}

      {wallet && (
        <>
          <p>üìä T·ªïng s·ªë ƒë∆°n h√†ng: <b>{orderCount}</b></p>

          {/* ===== FORM CREATE ORDER ===== */}
          <div style={styles.card}>
            <h3>‚ûï T·∫°o ƒë∆°n h√†ng</h3>

            <input
              placeholder="Product ID"
              value={productId}
              onChange={(e) => setProductId(e.target.value)}
              style={styles.input}
            />

            <input
              placeholder="Gi√° (VND)"
              value={amountVND}
              onChange={(e) => setAmountVND(e.target.value)}
              style={styles.input}
            />

            <button
              style={{ ...styles.button, marginTop: 10 }}
              onClick={handleCreateOrder}
            >
              Ghi Order l√™n Blockchain
            </button>

            {txHash && (
              <p style={{ fontSize: 12 }}>
                Tx:{' '}
                <a
                  href={`https://sepolia.etherscan.io/tx/${txHash}`}
                  target="_blank"
                  rel="noreferrer"
                >
                  {txHash.slice(0, 20)}...
                </a>
              </p>
            )}
          </div>
        </>
      )}

      {/* ===== LIST ORDER ===== */}
      {orders.map((o) => (
        <div key={o.orderId} style={styles.card}>
          <h3>üì¶ Order #{o.orderId}</h3>
          <p>üîß Product ID: {o.productId}</p>
          <p>üí∞ Gi√° (VND): {o.amountVND}</p>
          <p>üë§ Buyer: {o.buyer}</p>
          <p>üïí Th·ªùi gian: {o.createdAt}</p>
        </div>
      ))}
    </div>
  );
}

/* ================= STYLE ================= */

const styles = {
  container: {
    minHeight: '100vh',
    background: '#0f172a',
    color: '#e5e7eb',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center',
    padding: '40px'
  },
  title: { fontSize: '32px', color: '#38bdf8' },
  subtitle: { color: '#94a3b8', marginBottom: 20 },
  button: {
    padding: '10px 20px',
    borderRadius: 8,
    background: '#22c55e',
    border: 'none',
    cursor: 'pointer'
  },
  card: {
    background: '#020617',
    border: '1px solid #334155',
    borderRadius: 10,
    padding: 16,
    marginTop: 20,
    width: 400
  },
  input: {
    width: '100%',
    padding: 8,
    marginTop: 8,
    borderRadius: 6
  }
};

export default App;
