import './App.css';
import { useState } from 'react';
import { connectWallet } from './services/wallet';
import { getOrderCount, getOrderByIndex } from './services/blockchain';
import { ethers } from 'ethers';

function App() {
  const [wallet, setWallet] = useState('');
  const [orders, setOrders] = useState([]);
  const [orderCount, setOrderCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleConnectWallet = async () => {
    try {
      setLoading(true);
      setError('');

      // üîå K·∫æT N·ªêI V√ç
      const address = await connectWallet();
      if (!address) return;
      setWallet(address);

      // üîó CHECK NETWORK (SEPOLIA)
      const provider = new ethers.BrowserProvider(window.ethereum);
      const network = await provider.getNetwork();

      if (Number(network.chainId) !== 11155111) {
        throw new Error('Vui l√≤ng chuy·ªÉn sang m·∫°ng Sepolia');
      }

      // üìä L·∫§Y orderCount
      const countBN = await getOrderCount();
      const total = Number(countBN);
      setOrderCount(total);

      if (total === 0) {
        setOrders([]);
        return;
      }

      // üì¶ L·∫§Y DANH S√ÅCH ORDER (index b·∫Øt ƒë·∫ßu t·ª´ 1)
      const list = [];
      for (let i = 1; i <= total; i++) {
        const o = await getOrderByIndex(i);
        list.push({
          orderId: o.orderId.toString(),
          productId: o.productId.toString(),
          amountVND: o.amountVND.toString(),
          buyer: o.buyer,
          createdAt: new Date(
            Number(o.createdAt) * 1000
          ).toLocaleString('vi-VN')
        });
      }

      setOrders(list);
    } catch (err) {
      console.error(err);
      setError(err.message || '‚ùå Kh√¥ng ƒë·ªçc ƒë∆∞·ª£c d·ªØ li·ªáu t·ª´ blockchain');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>
        üîê H·ªÜ TH·ªêNG ƒê∆†N H√ÄNG / B·∫¢O H√ÄNH BLOCKCHAIN
      </h1>

      <p style={styles.subtitle}>
        Minh b·∫°ch d·ªØ li·ªáu ƒë∆°n h√†ng b·∫±ng Smart Contract
      </p>

      <button style={styles.button} onClick={handleConnectWallet}>
        {wallet
          ? `ƒê√£ k·∫øt n·ªëi: ${wallet.slice(0, 6)}...${wallet.slice(-4)}`
          : 'K·∫øt n·ªëi MetaMask'}
      </button>

      {loading && <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu t·ª´ blockchain...</p>}
      {error && <p style={{ color: '#ef4444' }}>{error}</p>}

      {wallet && !loading && (
        <p style={{ marginTop: 15 }}>
          üìä T·ªïng s·ªë ƒë∆°n h√†ng on-chain: <b>{orderCount}</b>
        </p>
      )}

      {wallet && orders.length === 0 && !loading && (
        <p style={{ marginTop: 20 }}>
          ‚ùå Ch∆∞a c√≥ ƒë∆°n h√†ng n√†o tr√™n blockchain
        </p>
      )}

      {orders.map((o) => (
        <div key={o.orderId} style={styles.card}>
          <h3>üì¶ Order #{o.orderId}</h3>
          <p>üîß Product ID: {o.productId}</p>
          <p>üí∞ Gi√° (VND): {o.amountVND}</p>
          <p>üë§ Buyer: {o.buyer}</p>
          <p>üïí Th·ªùi gian: {o.createdAt}</p>
        </div>
      ))}
    </div>
  );
}

/* ================= STYLE ================= */

const styles = {
  container: {
    minHeight: '100vh',
    background: '#0f172a',
    color: '#e5e7eb',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center',
    padding: '40px'
  },
  title: {
    fontSize: '32px',
    marginBottom: '10px',
    color: '#38bdf8'
  },
  subtitle: {
    fontSize: '16px',
    marginBottom: '30px',
    color: '#94a3b8'
  },
  button: {
    padding: '12px 24px',
    fontSize: '16px',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    background: '#22c55e',
    color: '#022c22',
    marginBottom: '20px'
  },
  card: {
    width: '100%',
    maxWidth: '520px',
    background: '#020617',
    border: '1px solid #334155',
    borderRadius: '10px',
    padding: '16px',
    marginBottom: '15px',
    textAlign: 'left'
  }
};

export default App;
