import './App.css';
import { useState } from 'react';
import { connectWallet } from './services/wallet';
import {
  getMyWarranties,
  getWarrantyDetail
} from './services/blockchain';

function App() {
  const [wallet, setWallet] = useState('');
  const [warranties, setWarranties] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleConnectWallet = async () => {
    setLoading(true);

    const address = await connectWallet();
    if (!address) {
      setLoading(false);
      return;
    }

    setWallet(address);

    // 1Ô∏è‚É£ L·∫•y danh s√°ch warrantyId
    const ids = await getMyWarranties(address);

    // 2Ô∏è‚É£ L·∫•y chi ti·∫øt t·ª´ng b·∫£o h√†nh
    const details = await Promise.all(
      ids.map(async (id) => {
        const w = await getWarrantyDetail(id);
        return {
          id: id.toString(),
          productId: w.productId.toString(),
          startTime: Number(w.startTime),
          endTime: Number(w.endTime)
        };
      })
    );

    setWarranties(details);
    setLoading(false);
  };

  const formatDate = (ts) =>
    new Date(ts * 1000).toLocaleDateString('vi-VN');

  const isExpired = (endTime) =>
    Date.now() / 1000 > endTime;

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>
        üîê H·ªÜ TH·ªêNG B·∫¢O H√ÄNH ƒêI·ªÜN T·ª¨ BLOCKCHAIN
      </h1>

      <p style={styles.subtitle}>
        Website qu·∫£n l√Ω b·∫£o h√†nh linh ki·ªán minh b·∫°ch b·∫±ng Blockchain
      </p>

      <button style={styles.button} onClick={handleConnectWallet}>
        {wallet
          ? `ƒê√£ k·∫øt n·ªëi: ${wallet.slice(0, 6)}...${wallet.slice(-4)}`
          : 'K·∫øt n·ªëi MetaMask'}
      </button>

      {loading && <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu b·∫£o h√†nh...</p>}

      {wallet && warranties.length === 0 && !loading && (
        <p style={{ marginTop: 20 }}>‚ùå V√≠ n√†y ch∆∞a c√≥ b·∫£o h√†nh n√†o</p>
      )}

      {/* DANH S√ÅCH B·∫¢O H√ÄNH */}
      {warranties.map((w) => (
        <div key={w.id} style={styles.card}>
          <h3>üîí Warranty #{w.id}</h3>
          <p>üì¶ Product ID: {w.productId}</p>
          <p>üóì Ng√†y b·∫Øt ƒë·∫ßu: {formatDate(w.startTime)}</p>
          <p>‚è∞ Ng√†y h·∫øt h·∫°n: {formatDate(w.endTime)}</p>
          <p>
            Tr·∫°ng th√°i:{' '}
            {isExpired(w.endTime) ? (
              <span style={{ color: '#ef4444' }}>‚ùå H·∫øt h·∫°n</span>
            ) : (
              <span style={{ color: '#22c55e' }}>‚úÖ C√≤n h·∫°n</span>
            )}
          </p>
        </div>
      ))}
    </div>
  );
}

/* ================= STYLE ================= */

const styles = {
  container: {
    minHeight: '100vh',
    background: '#0f172a',
    color: '#e5e7eb',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center',
    padding: '40px'
  },
  title: {
    fontSize: '32px',
    marginBottom: '10px',
    color: '#38bdf8'
  },
  subtitle: {
    fontSize: '16px',
    marginBottom: '30px',
    color: '#94a3b8'
  },
  button: {
    padding: '12px 24px',
    fontSize: '16px',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    background: '#22c55e',
    color: '#022c22',
    marginBottom: '20px'
  },
  card: {
    width: '100%',
    maxWidth: '500px',
    background: '#020617',
    border: '1px solid #334155',
    borderRadius: '10px',
    padding: '16px',
    marginBottom: '15px',
    textAlign: 'left'
  }
};

export default App;
